import React from "react";
import styled from "styled-components";
import ReflectionCard from "../cards/ReflectionCard";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-top: 50px;
  padding: 0px 16px;
  position: relative;
  z-index: 1;
  align-items: center;
`;

const Wrapper = styled.div`
  position: relative;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  width: 100%;
  max-width: 1100px;
  gap: 12px;
  @media (max-width: 960px) {
    flex-direction: column;
  }
`;

const Title = styled.div`
  font-size: 52px;
  text-align: center;
  font-weight: 600;
  margin-top: 20px;
  color: ${({ theme }) => theme.text_primary};
  @media (max-width: 768px) {
    margin-top: 12px;
    font-size: 32px;
  }
`;

const Desc = styled.div`
  font-size: 18px;
  text-align: center;
  font-weight: 600;
  color: ${({ theme }) => theme.text_secondary};
  @media (max-width: 768px) {
    font-size: 16px;
  }
`;

const CardsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  justify-content: center;
  margin-top: 20px;
`;

const reflections = [
  {
    title: "Reflection 1",
    date: "2023-01-01",
    description: "This is a brief description of reflection 1.",
  },
  {
    title: "Reflection 2",
    date: "2023-02-01",
    description: "This is a brief description of reflection 2.",
  },
  {
    title: "Reflection 2",
    date: "2023-02-01",
    description: "This is a brief description of reflection 2.",
  },
  // Add more reflections as needed
];

const Reflections = () => {
  return (
    <Container>
      <Wrapper>
        <Title>Reflections</Title>
        <Desc>
          Here are some reflections on my journey, experiences, and lessons learned.
        </Desc>
        <CardsContainer>
          {reflections.map((reflection, index) => (
            <ReflectionCard key={index} reflection={reflection} />
          ))}
        </CardsContainer>
      </Wrapper>
    </Container>
  );
};

export default Reflections;
